import React, { Component, Suspense, lazy } from 'react'
import { Signin } from './Signin'
import { Header } from './Header'
import { TodoList } from './TodoList'
import { ThemeProvider, theme, CSSReset, ToastProvider } from '@blockstack/ui'
import { Session } from '../auth'
import ReactDOM from 'react-dom'

// ** Redux Imports
import { Provider } from 'react-redux'
import { store } from '../redux/store'

// ** Intl, CASL & ThemeColors Context
import ability from '../configs/acl/ability'
import { ToastContainer } from 'react-toastify'
import { AbilityContext } from '../utility/context/Can'
import { ThemeContext } from '../utility/context/ThemeColors'

// ** i18n
import '../configs/i18n'

// ** Spinner (Splash Screen)
import Spinner from '../@core/components/spinner/Fallback-spinner'

// ** Ripple Button
import '../@core/components/ripple-button'

// ** Fake Database
import '../@fake-db'

// ** PrismJS
import '../prismjs'
import '../prismjs/themes/prism-tomorrow.css'
import '../prismjs/components/prism-jsx.min'

// ** React Perfect Scrollbar
import '../react-perfect-scrollbar/dist/css/styles.css'

// ** React Toastify
import '../@core/scss/react/libs/toastify/toastify.scss'

// ** Core styles
import '../@core/assets/fonts/feather/iconfont.css'
import '../@core/scss/core.scss'
import '../assets/scss/style.scss'

// ** Service Worker
import * as serviceWorker from './serviceWorker'

// ** Lazy load app
const LazyApp = lazy(() => import('./App'))

export default class App extends Component {

  state = {
    userData: null
  }

  handleSignOut(e) {
    e.preventDefault()
    this.setState({ userData: null })
    Session.signUserOut(window.location.origin)
  }

  render() {
    return (
      <ThemeProvider theme={theme}>
        <ToastProvider>
        <Suspense fallback={<Spinner />}>
        <AbilityContext.Provider value={ability}>
          <ThemeContext>
              <Header />
              {!Session.isUserSignedIn() ? <Signin /> : <LazyApp />}
              </ThemeContext>
        </AbilityContext.Provider>
      </Suspense>
        </ToastProvider>
        <CSSReset />
      </ThemeProvider>
    )
  }

  componentDidMount() {
    if (Session.isSignInPending()) {
      Session.handlePendingSignIn().then(userData => {
        window.history.replaceState({}, document.title, '/')
        this.setState({ userData })
      })
    } else if (Session.isUserSignedIn()) {
      this.setState({ userData: Session.loadUserData() })
    }
  }
}
  // If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister()
