// ** Reactstrap Imports
import { Input } from 'reactstrap'

const PaymentHeader = ({ duration, setDuration }) => {
  const onChange = e => {
    if (e.target.checked) {
      setDuration('yearly')
    } else {
      setDuration('monthly')
    }
  }

  return (
    <div className='text-center'>
      <h1 className='mt-5'>Pay and Publish</h1>
      <p className='mb-2 pb-75'>
        Give your ad a budget as small as  1 Fari or its equivalent in STX. 
        In terms of USD, the minimum budgets is currently about 10 cents.
      </p>
    </div>
  )
}

export default PaymentHeader
