// ** React Imports
import { useState, useEffect, Fragment } from 'react'

// ** Third Party Components
import axios from 'axios'

// ** Demo Components
import PaymentFaqs from './PaymentFaqs'
import PaymentCards from './PaymentCards'
import PaymentTrial from './PaymentTrial'
import PaymentHeader from './PaymentHeader'

// ** Styles
import '@styles/base/pages/page-pricing.scss'

const Payment = () => {
  // ** States
  const [data, setData] = useState(null),
    [faq, setFaq] = useState(null),
    [duration, setDuration] = useState('monthly')

  useEffect(() => {
    axios.get('/pricing/data').then(res => {
      const dataArr = [],
        faqArr = []

      Object.entries(res.data).forEach(([key, val]) => {
        if (key !== 'qandA') {
          dataArr.push(val)
          setData([...dataArr])
        } else {
          faqArr.push(val)
          setFaq(faqArr[0])
        }
      })
    })
  }, [])

  return (
    <div id='payment-table'>
      <PaymentHeader />
     
        <Fragment>
          <PaymentCards />
          <PaymentTrial />
          <PaymentFaqs data={faq} />
        </Fragment>

    </div>
  )
}

export default Payment
