import React, { Component, lazy } from 'react'
import { Signin } from './Signin'
import { Header } from './Header'
import { Session } from '../auth'
import ReactDOM from 'react-dom'

// ** Redux Imports
import { Provider } from 'react-redux'
import { store } from '../redux/store'

// ** Intl, CASL & ThemeColors Context
import ability from '../configs/acl/ability'
import { AbilityContext } from '../utility/context/Can'
import { ThemeContext } from '../utility/context/ThemeColors'

// ** i18n
import '../configs/i18n'

// ** Ripple Button
import '../@core/components/ripple-button'

// ** Fake Database
import '../@fake-db'

// ** React Toastify
import '../@core/scss/react/libs/toastify/toastify.scss'

// ** Core styles
import '../@core/assets/fonts/feather/iconfont.css'
import '../@core/scss/core.scss'
import '../assets/scss/style.scss'

// ** Lazy load app
const LazyApp = lazy(() => import('./App'))

export default class App extends Component {

  state = {
    userData: null
  }

  handleSignOut(e) {
    e.preventDefault()
    this.setState({ userData: null })
    Session.signUserOut(window.location.origin)
  }

  render() {
    return (
      <Provider store={store}>
        <AbilityContext.Provider value={ability}>
          <ThemeContext>
            <Header />
            {!Session.isUserSignedIn() ? <Signin /> : <LazyApp />}
          </ThemeContext>
        </AbilityContext.Provider>
      </Provider>
       )
  }

  componentDidMount() {
    if (Session.isSignInPending()) {
      Session.handlePendingSignIn().then(userData => {
        window.history.replaceState({}, document.title, '/')
        this.setState({ userData })
      })
    } else if (Session.isUserSignedIn()) {
      this.setState({ userData: Session.loadUserData() })
    }
  }
}
