import React, { Component } from 'react'
import { Signin } from './Signin'
import { Header } from './Header'
import { TodoList } from './TodoList';
import { ThemeProvider, theme, CSSReset, ToastProvider } from '@blockstack/ui'
import { Session } from '../auth'

export default class App extends Component {
  state = {
    userData: null
  }

  handleSignOut(e) {
    e.preventDefault()
    this.setState({ userData: null })
    Session.signUserOut(window.location.origin)
  }

  render() {
    return (
      <ThemeProvider theme={theme}>
        <ToastProvider>
          <div className="site-wrapper">
            <div className="site-wrapper-inner">
              <Header />
              {!Session.isUserSignedIn() ? <Signin /> : <TodoList />}
            </div>
          </div>
        </ToastProvider>
        <CSSReset />
      </ThemeProvider>
    )
  }

  componentDidMount() {
    if (Session.isSignInPending()) {
      Session.handlePendingSignIn().then(userData => {
        window.history.replaceState({}, document.title, '/')
        this.setState({ userData })
      })
    } else if (Session.isUserSignedIn()) {
      this.setState({ userData: Session.loadUserData() })
    }
  }
}
