// ** React Imports
import { Fragment } from 'react'

// ** Third Party Components
import 'cleave.js/dist/addons/cleave-phone.us'

// ** Reactstrap Imports
import {  Card, CardBody, CardTitle, CardHeader } from 'reactstrap'

// ** Stacks Transactions
import { bufferCVFromString, callReadOnlyFunction } from '@stacks/transactions'
import { StacksTestnet } from '@stacks/network'
 

// Auth Details
// const address = Session.address.toString()

// STX Query 
const contractAddress = 'ST39EMTZG4P7D55FMEQXEB8ZEQEK0ECBHB1J6QD63'
const contractName = 'vip-burn'
const functionName = 'get-last-token-id'
const buffer = bufferCVFromString('foo')
const network = new StacksTestnet()
const senderAddress = 'ST39EMTZG4P7D55FMEQXEB8ZEQEK0ECBHB1J6QD63'

const options = {
  contractAddress,
  contractName,
  functionName,
  functionArgs: [buffer],
  network,
  senderAddress
}

const walletFaris = async () => { await callReadOnlyFunction(options) }

const AccountVal  = 'YOU HAVE **' + { walletFaris } + '** FARIS'  

export default AccountVal
