import { useRef, useState } from 'react'
import Wizard from '@components/wizard'
import { ArrowRight } from 'react-feather'
import Address from './steps-with-validation/Address'
import PersonalInfo from './steps-with-validation/PersonalInfo'
import AccountDetails from './steps-with-validation/AccountDetails'

const WizardHorizontal = () => {
  const [stepper, setStepper] = useState(null)
  const ref = useRef(null)

  const steps = [
    {
      id: 'account-details',
      title: 'Screen Details',
      subtitle: 'Register Your Screen.',
      content: <AccountDetails stepper={stepper} type='wizard-horizontal' />
    },
    {
      id: 'personal-info',
      title: 'Payment Info',
      subtitle: 'Add Wallet Address',
      content: <PersonalInfo stepper={stepper} type='wizard-horizontal' />
    },
    {
      id: 'step-address',
      title: 'Location',
      subtitle: 'Add Screen Location',
      content: <Address stepper={stepper} type='wizard-horizontal' />
    }
  ]

  return (
    <div className='horizontal-wizard'>
      <Wizard instance={el => setStepper(el)} ref={ref} steps={steps} />
    </div>
  )
}

export default WizardHorizontal
